#4.two limits and print the putput palindrome
def isPalindrome(n: int) -> bool: 
    rev = 0
    i = n 
    while i > 0: 
        rev = rev * 10 + i % 10
        i //= 10 
    return (n == rev) 

def countPal(minn: int, maxx: int) -> None: 
    c=0
    for i in range(minn, maxx + 1): 
        if isPalindrome(i): 
            c+=1 
    print(c)
x=int(input())
y=int(input())
countPal(x,y)

